#include <iostream>
#include <cstring>
#include <string>

using namespace std;

int m[10240]; //[10240];
int m_tmp[10240];
int acc[10240];

void print(int x, int * m) {
    for(int i = 0; i <= 6; ++i) {
        cout << m[i] << " ";
    }
    cout << endl;
}

class Solution {
public:
    bool isMatch(string s, string p) {
        memset(m, 0, sizeof(m));
        memset(m_tmp, 0, sizeof(m_tmp));
        memset(acc, 0, sizeof(acc));
        int ls = s.size();
        int lp = p.size();
        m_tmp[0] = 1;
        acc[0] = 1;
        for(int j = 1; j <= lp; ++j) {
            if(p[j - 1] == '*') {
                m_tmp[j] = m_tmp[j - 1];
            } 
            acc[j] += m_tmp[j];
        }
        //print(0, m_tmp);
        for(int i = 1; i <= ls; ++i) {
            memset(m, 0, sizeof(m));
            for(int j = 1; j <= lp; ++j) {
                if(s[i - 1] == p[j - 1] || p[j - 1] == '?') {
                    //cout << i << " " << j << " here" << endl;
                    m[j] = m_tmp[j - 1];
                } else if(p[j - 1] == '*') {
                    //cout << acc[j - 1] << " here " << i << " " << j  << endl;
                    if(acc[j - 1] > 0) m[j] = 1;
                }
                acc[j] += m[j];
            }
            //print(i, m);
            memcpy(m_tmp, m, sizeof(int) * (lp + 1));
        }
        return m_tmp[lp];
    }
};


int main() {
    Solution s;
    //std::string a("aaa");
    //std::string b("*aaaa*");
    string a = "";
    string b = "**";

    cout << s.isMatch(a, b) << endl;
    //cout << s.isMatch("aab", "c*a*b*") << endl;
    return 0;
}
    


