#include <iostream>
#include <cstring>
#include <string>

using namespace std;

bool m[10000][10000];

void print() {
    for(int i = 0; i < 4; ++i) {
        for(int j = 0; j < 4; ++j) {
            cout << m[i][j] << " ";
        }
        cout << endl;
    }
    cout << endl;
}

class Solution {
public:
    bool isMatch(string s, string p) {
        memset(m, 0, sizeof(m));
        int ls = s.size();
        int lp = p.size();
        m[0][0] = 1;
        for(int j = 1; j <= lp; ++j) {
            if(p[j - 1] == '*') {
                m[0][j] = m[0][j - 1];
            } 
        }
        for(int i = 1; i <= ls; ++i) {
            for(int j = 1; j <= lp; ++j) {
                if(s[i - 1] == p[j - 1] || p[j - 1] == '?') {
                    //cout << i << " " << j << " here" << endl;
                    m[i][j] = m[i - 1][j - 1];
                } else if(p[j - 1] == '*') {
                    int k = i;
                    while(m[k][j - 1] == 0 && k >= 0) --k;
                    if(k >= 0) m[i][j] = 1;
                }
            }
            //print();
        }
        return m[ls][lp];
    }
};

string a = "";
string b = "**";


int main() {
    Solution s;
    cout << a.size() << " " << b.size() << endl;
    //std::string a("aab");
    //std::string b("aab");
    cout << s.isMatch(a, b) << endl;
    //print();
    return 0;
}




